function NavToggle() {
	$(".navbar-minimalize").trigger("click")
}

function SmoothlyMenu() {
	$("body").hasClass("mini-navbar") ? $("body").hasClass("fixed-sidebar") ? ($("#side-menu").hide(), setTimeout(function() {
		$("#side-menu").fadeIn(500)
	}, 100)) : $("#side-menu").removeAttr("style") : ($("#side-menu").hide(), setTimeout(function() {
		$("#side-menu").fadeIn(500)
	}, 100))
}

function localStorageSupport() {
	return "localStorage" in window && null !== window.localStorage
}

$(document).ready(function() {
	
	/* 左侧菜单js */
	$("#side-menu").metisMenu();
	
	/* 左侧菜单超出屏幕高度滚动条 */
	$(".sidebar-collapse").slimScroll({
		height: "100%",
		railOpacity: .9,
		alwaysVisible: !1
	});


	/* 左侧菜单超出屏幕高度滚动条 */
	$("#content-main").slimScroll({
		size: "7px",
		height: $(this).height()-97,
		railOpacity: .4,
		alwaysVisible: !1
	});
	

	/* 展开收起左侧菜单栏 */
	$(".navbar-minimalize").click(function() {
		$("body").toggleClass("mini-navbar");
			SmoothlyMenu();
	});
	
	/* 分辨率小于769,点击一级菜单展开菜单列表 */
	$("#side-menu>li").click(function() {
		if($("body").hasClass("mini-navbar"))
			NavToggle();
	});
	
	/* 屏幕分辨率小于350,左侧菜单显示关闭按钮 */
	$(".nav-close").click(NavToggle), /(iPhone|iPad|iPod|iOS)/i.test(navigator.userAgent) && $("#content-main").css("overflow-y", "auto")
});

/* 自适应页面 */
$(window).bind("load resize", function() {
	if($(this).width() < 769){
		$("body").addClass("mini-navbar");
		$(".navbar-static-side").fadeIn();
	}
});